mapper.select:
SELECT count(li.id) operationNum, le.errorNum , ll.loginNum
FROM log_info li
INNER JOIN(
    SELECT count(1) errorNum FROM log_error_info


            AND user_id = #{dto.userId}


) le INNER JOIN(
    SELECT count(1) loginNum FROM login_log


            AND user_id = #{dto.userId}


) ll


        and li.user_id = #{dto.userId}
